package com.sample.loopingstatements;

public class DoWhileLoopSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Printing the first ten numbers using do-while loop ... ");
		int value =1;
		do {
			System.out.println(value);
			value++;
		}while(value <= 10);
	}

}
