package com.sample.loopingstatements;

public class WhileLoopSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Printing the first ten numbers using while loop ... ");
		int value = 1;
		while(value <= 10) {
			System.out.println(value);
			value++;
		}
	}

}
