package com.sample.stringconcepts;

/*
Returns the Length of the String which means the number of characters including Blank Space in the string.
StringBuffer Length method has been implemented from Char Sequence interface.
*/

public class StringBufferLength {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		StringBuffer s= new StringBuffer("String Buffer");
	    System.out.println(s);
	    
	    int a=s.length();
	    System.out.println(a);

	}

}
