package com.sample.stringconcepts;

/*
Checks whether the string is Empty.Return type is boolean.
Returns True if the String Length is 0(Zero), if not returns false.
*/

public class StringIsEmpty {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a="";
		String b="Java";
		
		boolean s=a.isEmpty();
		System.out.println(s);
		
		boolean s1=b.isEmpty();
		System.out.println(s1);

	}

}
