package com.sample.stringconcepts;

/*
Checks whether the string starts with specified Character.Return type is boolean.
Returns True if the string starts with the specified character, if not returns false.
*/

public class StringStartsWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Core Java";
	     
		boolean a=s.startsWith("C");
		System.out.println(a);
		
		boolean b=s.startsWith("j");
		System.out.println(b);

	}

}
